import json

import json

def sort_sets(data):
    return {k: dict(sorted(v.items(), key=lambda x: int(x[0]))) for k, v in data.items()}

def map_subject_set(setA_data, mapping_df):
    # Normalize keys to int
    setA_data = {int(k): v for k, v in setA_data.items()}
    mapped = {"setA": {}, "setB": {}, "setC": {}}

    # Copy Set A as-is
    mapped["setA"] = {str(k): v for k, v in setA_data.items()}

    # Build reverse maps (A ‚Üí B and A ‚Üí C)
    for _, row in mapping_df.iterrows():
        a = int(row["SET A"])
        b = int(row["SET B"])
        c = int(row["SET C"])

        # Map questions based on Set A content
        if a in setA_data:
            if b != 0:
                mapped["setB"][str(b)] = setA_data[a]
            if c != 0:
                mapped["setC"][str(c)] = setA_data[a]

    # Sort for nice ordering
    return sort_sets(mapped)

def apply_set_mappings(merged_data, mappings, verbose=False):
    """Apply set mappings to all subjects."""
    final_output = {}

    for subject, mapping_df in zip(merged_data.keys(), mappings):
        if verbose:
            print(f"\nüîπ Subject: {subject}")
            print(mapping_df)

        final_output[subject] = map_subject_set(merged_data[subject]["setA"], mapping_df)

    print("\n‚úÖ All subjects processed.")
    print(json.dumps(final_output, indent=2 , ensure_ascii=False))
    return final_output




